files.controllers/featureController.js:
// controllers/featureController.js
import { isFeatureEnabled } from '../models/featureModel.js';
import { renderFeature } from '../views/featureView.js';

export const handleFeatureRequest = (featureName) => {
    if (isFeatureEnabled(featureName)) {
        renderFeature(featureName);
    } else {
        console.log(`Feature ${featureName} is disabled.`);
    }
};

