// models/featureModel.js
import { featureToggles } from '../config/featureToggles.js';

export const isFeatureEnabled = (featureName) => {
    return featureToggles[featureName] || false;
};

files.views/featureView.js:
// views/featureView.js
export const renderFeature = (featureName) => {
    console.log(`Rendering feature: ${featureName}`);
};

