files.assets/css/styles.css:
/* assets/css/styles.css */
body {
    font-family: Arial, sans-serif;
    margin: 0;
    padding: 0;
}
.header, .footer {
    background-color: #333;
    color: white;
    text-align: center;
    padding: 1em 0;
}
.hero {
    background: url('../images/hero-bg.jpg') no-repeat center center;
    height: 400px;
    display: flex;
    align-items: center;
    justify-content: center;
    color: white;
}

